$color-input-border: #d3d3d3;
$color-button: #d3d3d3;

$color-text: #272727;
$color-msg-error: #8b0000;

$shadow: 0px 0px 1px 1px $color-shadow;

/*FONTS-SIZES-WEIGHTS*/
$font-size-general: 62.5%;
$font-family: 'quicksand';
$font-family-alt: 'roboto';

$font-size-header: 1.6rem;
$font-weight-header: 100;

$font-size-subheader: 1.2rem;
$font-weight-subheader: 300;

$font-size-p: .85rem;
$font-weight-p: 300;

