  margin-top: 20vh;
  background: linear-gradient(to bottom, rgba($color-login, .9), rgba($color-login, .7));

  padding: 2rem;
  border-radius: .125rem;
  box-shadow: $shadow;

  &-instruction {
    font-family: $font-family;
    font-size: $font-size-subheader;
    font-weight: $font-weight-subheader;
    margin-bottom: 1.5rem;
  }

  &-controls {
    display: flex;
    flex-direction: column;

    &-control {
      width: 100%;
      display: flex;
      flex-direction: row;
      justify-content: space-evenly;
      margin-bottom: .5rem;

      &-description {
        width: 30%;

        font-family: $font-family;
        font-size: $font-size-p;
        font-weight: $font-weight-p;

        margin-bottom: .2rem;
        margin-right: .5rem;
      }

      input[type="text"], input[type="password"] {
        width: 70%;
        font-family: $font-family-alt;
      }
    }

    &-description-error {
      color: $color-msg-error;
      text-align: center;
      padding: .3rem 0;

      font-family: $font-family;
      font-size: $font-size-p;
      font-weight: bold;
    }
  }
}

