  min-height: calc(100vh - 3rem - 5vh);

  .lookup {
    height: 8%;
    width: 100%;
    margin-left: 3rem;
    margin-top: 1rem;

    font-family: $font-family;
    font-size: $font-size-subheader;
    font-weight: $font-weight-subheader;

    display: flex;
    flex-wrap: wrap;
    flex-direction: row;
    justify-content: flex-start;
    align-items: flex-start;

    &-description {
      width: 100%;
      margin-top: .8rem;
      margin-right: .75rem;
    }

    &-bar {
      width: 60%;
      margin-top: .8rem;
      padding-left: 1.5rem;
      padding-top: .5rem;
      padding-bottom: .5rem;

    }

    &-button {
      margin-left: .75rem;
      width: 15%;
    }
  }

  .results {
    min-height: 92%;
    padding: 0 2rem 2rem 2rem;

    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    justify-content: flex-start;

    &-message {
      font-family: $font-family;
      font-size: $font-size-subheader;
      font-weight: $font-weight-p;
      font-style: italic;

      margin: 2rem 0 0 1rem;
    }

    .musician {
      display: flex;
      flex-direction: column;
      justify-content: flex-start;
      align-items: center;

      margin: 1.5rem 1rem 0.5rem 1rem;
      padding: .5rem 1rem 1rem 1rem;
      background: linear-gradient(to bottom, rgba($color-bg, .65), rgba($color-bg, .2));

      border-bottom-right-radius: 1rem;
      border-bottom-left-radius: 1rem;

      animation: popIn 300ms 150ms;
      animation-fill-mode: forwards;
      animation-timing-function: cubic-bezier(0.64, 0.57, 0.67, 1.53);
      transform: scale(0);

      transition: all .3s ease-out;

      &-link {
        width: 100%;
        display: flex;
        flex-direction: column;
        align-items: center;

        &-title {
          font-family: $font-family-alt;
          font-size: $font-size-subheader;
          font-weight: $font-weight-header;
        }

        &-info {

          &-listeners {
            font-family: $font-family-alt;
            font-size: $font-size-p;
            font-weight: $font-weight-p;
            font-style: italic;
            margin-top: .4rem;
            margin-bottom: .5rem;
          }
        }

        img {
          border-radius: 1rem;
        }
      }

      &:hover {
        background: linear-gradient(to bottom, rgba(72, 64, 85, .45), rgba(239, 169, 248, .18));

        .musician-link-title span,
        .musician-link-info-listeners,
        .musician-link-info-listeners span {
          color: white;
        }
      }

      &-remove {
        margin-top: 1.2rem;
        width: 7.5vw;
        align-self: center !important;
      }      
    }
  }
}

